import{_ as i,c as e,a2 as t,o as a}from"./chunks/framework.DRc6tsBz.js";const s=JSON.parse('{"title":"VSCode","description":"","frontmatter":{"title":"VSCode","tags":["VSCode"],"comments":true,"date":"2023-05-21T21:10:32.000Z"},"headers":[],"relativePath":"misc/cheatsheet/vscode.md","filePath":"misc/cheatsheet/vscode.md","lastUpdated":1715308956000}');const o=i({name:"misc/cheatsheet/vscode.md"},[["render",function(i,s,o,l,d,n){return a(),e("div",null,s[0]||(s[0]=[t('<h2 id="shortcuts" tabindex="-1">Shortcuts <a class="header-anchor" href="#shortcuts" aria-label="Permalink to &quot;Shortcuts&quot;">​</a></h2><h3 id="general" tabindex="-1">General <a class="header-anchor" href="#general" aria-label="Permalink to &quot;General&quot;">​</a></h3><ul><li>Show Command Palette: <code>⇧⌘P, F1</code></li><li>Quick Open, Go to File...: <code>⌘P</code></li><li>User Settings: <code>⌘,</code></li></ul><h3 id="basic-editing" tabindex="-1">Basic editing <a class="header-anchor" href="#basic-editing" aria-label="Permalink to &quot;Basic editing&quot;">​</a></h3><ul><li>Toggle line comment: <code>⌘/</code></li></ul><h3 id="search-and-replace" tabindex="-1">Search and replace <a class="header-anchor" href="#search-and-replace" aria-label="Permalink to &quot;Search and replace&quot;">​</a></h3><ul><li>Find: ⌘F</li><li>Select all occurrences of Find match: ⌥Enter</li><li>Add selection to next Find match: ⌘D</li></ul><h3 id="rich-languages-editing" tabindex="-1">Rich languages editing <a class="header-anchor" href="#rich-languages-editing" aria-label="Permalink to &quot;Rich languages editing&quot;">​</a></h3><ul><li>Trigger suggestion: <code>⌃Space, ⌘I</code></li><li>Go to Definition: <code>F12</code></li><li>Peek Definition: <code>⌥F12</code></li><li>Quick Fix: <code>⌘.</code></li><li>Rename Symbol: <code>F2</code></li></ul><h3 id="navigation" tabindex="-1">Navigation <a class="header-anchor" href="#navigation" aria-label="Permalink to &quot;Navigation&quot;">​</a></h3><ul><li>Show all Symbols: <code>⌘T</code></li><li>Go to Line...: <code>⌃G</code></li><li>Go to File...: <code>⌘P</code></li><li>Go to Symbol...: <code>⇧⌘O</code></li><li>Go to next/previous error or warning: <code>F8/⇧F8</code></li></ul><h3 id="display" tabindex="-1">Display <a class="header-anchor" href="#display" aria-label="Permalink to &quot;Display&quot;">​</a></h3><ul><li>Toggle Sidebar visibility: <code>⌘B</code></li><li>Toggle Panel visibility: <code>⌘J</code></li><li>Focus into Primary Side Bar: <code>⌘0</code></li><li>Focus First Editor Group: <code>⌘1</code></li></ul><h3 id="debug" tabindex="-1">Debug <a class="header-anchor" href="#debug" aria-label="Permalink to &quot;Debug&quot;">​</a></h3><ul><li>Toggle breakpoint: <code>F9</code></li><li>Start/Continue: <code>F5</code></li><li>Step into/ out: <code>F11 / ⇧F11</code></li><li>Step over: <code>F10</code></li></ul><h3 id="integrated-terminal" tabindex="-1">Integrated terminal <a class="header-anchor" href="#integrated-terminal" aria-label="Permalink to &quot;Integrated terminal&quot;">​</a></h3><ul><li>Show integrated terminal: `⌃``</li><li>Scroll up/down: <code>⌘↑ / ↓</code></li></ul><h2 id="recommand-settings" tabindex="-1">Recommand Settings <a class="header-anchor" href="#recommand-settings" aria-label="Permalink to &quot;Recommand Settings&quot;">​</a></h2><div class="language-json vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">json</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">{</span></span>\n<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">  &quot;editor.cursorSurroundingLines&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">8</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">// 光标周围显示行数</span></span>\n<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">  &quot;editor.fontFamily&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;JetBrains Mono&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">// 为程序员设计的字体</span></span>\n<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">  &quot;editor.formatOnSave&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">true</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">// 保存的时候自动格式化</span></span>\n<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">  &quot;files.trimTrailingWhitespace&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">true</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">// 去掉结束的空格</span></span>\n<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">  &quot;files.autoSaveDelay&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">100</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">// 自动延时保存</span></span>\n<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">  &quot;files.autoSave&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;afterDelay&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">// 自动保存</span></span>\n<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">  &quot;vim.leader&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;">&quot;,&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">// 设置Vim的leader键</span></span>\n<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">  &quot;terminal.integrated.copyOnSelection&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">true</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">, </span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;">// 选中自动复制</span></span>\n<span class="line"><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">  &quot;terminal.integrated.scrollback&quot;</span><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">: </span><span style="--shiki-light:#005CC5;--shiki-dark:#79B8FF;">1000000</span><span style="--shiki-light:#6A737D;--shiki-dark:#6A737D;"> // 终端滚动行数</span></span>\n<span class="line"><span style="--shiki-light:#24292E;--shiki-dark:#E1E4E8;">}</span></span></code></pre></div><h2 id="tips-and-tricks" tabindex="-1">Tips and Tricks <a class="header-anchor" href="#tips-and-tricks" aria-label="Permalink to &quot;Tips and Tricks&quot;">​</a></h2><h3 id="命令太多" tabindex="-1">命令太多 <a class="header-anchor" href="#命令太多" aria-label="Permalink to &quot;命令太多&quot;">​</a></h3><p>用<code>⇧⌘P, F1</code>去搜索，会面会显示快捷键</p><h3 id="大文件滚动太慢" tabindex="-1">大文件滚动太慢 <a class="header-anchor" href="#大文件滚动太慢" aria-label="Permalink to &quot;大文件滚动太慢&quot;">​</a></h3><p>按住<code>⌘</code>键在滚动鼠标滚轮</p><h3 id="各种命名风格转换" tabindex="-1">各种命名风格转换 <a class="header-anchor" href="#各种命名风格转换" aria-label="Permalink to &quot;各种命名风格转换&quot;">​</a></h3><p>VSCode 命令：</p><ul><li>Transform to Camel Case(驼峰命名法)</li><li>Transform to Kebab Case(串式命名法)</li><li>Transform to Snake Case(蛇形命名法)</li><li>Transform to Title Case(首字母大写)</li><li>Transform to Lower Case(小写)</li><li>Transform to Upper Case(大写)</li></ul><p>// TODO</p><h2 id="plugins" tabindex="-1">Plugins <a class="header-anchor" href="#plugins" aria-label="Permalink to &quot;Plugins&quot;">​</a></h2><h3 id="vscodevim" tabindex="-1">VSCodeVim <a class="header-anchor" href="#vscodevim" aria-label="Permalink to &quot;VSCodeVim&quot;">​</a></h3><p>VSCode 的 Vim 插件实现了一些 Vim 插件的功能</p><h4 id="vim-surround" tabindex="-1">vim-surround <a class="header-anchor" href="#vim-surround" aria-label="Permalink to &quot;vim-surround&quot;">​</a></h4><table tabindex="0"><thead><tr><th>Surround Command</th><th>Description</th></tr></thead><tbody><tr><td><code>y s &lt;motion&gt; &lt;desired&gt;</code></td><td>Add <code>desired</code> surround around text defined by <code>&lt;motion&gt;</code></td></tr><tr><td><code>d s &lt;existing&gt;</code></td><td>Delete <code>existing</code> surround</td></tr><tr><td><code>c s &lt;existing&gt; &lt;desired&gt;</code></td><td>Change <code>existing</code> surround to <code>desired</code></td></tr><tr><td><code>S &lt;desired&gt;</code></td><td>Surround when in visual modes (surrounds full selection)</td></tr></tbody></table><p>Some examples:</p><ul><li><code>&quot;test&quot;</code> with cursor inside quotes type <code>cs&quot;&#39;</code> to end up with <code>&#39;test&#39;</code></li><li><code>&quot;test&quot;</code> with cursor inside quotes type <code>ds&quot;</code> to end up with <code>test</code></li><li><code>&quot;test&quot;</code> with cursor inside quotes type <code>cs&quot;t</code> and enter <code>123&gt;</code> to end up with <code>&lt;123&gt;test&lt;/123&gt;</code></li></ul><h4 id="vim-commentary" tabindex="-1">vim-commentary <a class="header-anchor" href="#vim-commentary" aria-label="Permalink to &quot;vim-commentary&quot;">​</a></h4><p>加注释: Line和Block</p><ul><li><code>gc</code> - toggles line comment. For example gcc</li><li><code>gC</code> - toggles block comment. For example gCi)</li></ul><h4 id="vim-sneak" tabindex="-1">vim-sneak <a class="header-anchor" href="#vim-sneak" aria-label="Permalink to &quot;vim-sneak&quot;">​</a></h4><p>转跳连续的两个字符</p><table tabindex="0"><thead><tr><th>Motion Command</th><th>Description</th></tr></thead><tbody><tr><td><code>s&lt;char&gt;&lt;char&gt;</code></td><td>Move forward to the first occurrence of <code>&lt;char&gt;&lt;char&gt;</code></td></tr><tr><td><code>S&lt;char&gt;&lt;char&gt;</code></td><td>Move backward to the first occurrence of <code>&lt;char&gt;&lt;char&gt;</code></td></tr><tr><td><code>&lt;operator&gt;z&lt;char&gt;&lt;char&gt;</code></td><td>Perform <code>&lt;operator&gt;</code> forward to the first occurrence of <code>&lt;char&gt;&lt;char&gt;</code></td></tr><tr><td><code>&lt;operator&gt;Z&lt;char&gt;&lt;char&gt;</code></td><td>Perform <code>&lt;operator&gt;</code> backward to the first occurrence of <code>&lt;char&gt;&lt;char&gt;</code></td></tr></tbody></table><h3 id="github-copilot" tabindex="-1">GitHub Copilot <a class="header-anchor" href="#github-copilot" aria-label="Permalink to &quot;GitHub Copilot&quot;">​</a></h3><ul><li>Trigger inline suggestion: <code>Option: (⌥)+\\</code></li><li>Open GitHub Copilot: <code>Ctrl+Return</code></li><li>Show next inline suggestion: <code>Option (⌥)+]</code></li><li>Show previous inline suggestion: <code>Option (⌥)+[</code></li></ul>',43)]))}]]);export{s as __pageData,o as default};
