import{_ as t,c as i,a2 as e,o as a}from"./chunks/framework.DRc6tsBz.js";const o=JSON.parse('{"title":"【Git】理解 Git","description":"","frontmatter":{"title":"【Git】理解 Git","categories":"Git","tags":["Git"],"sidebar":false,"prev":false,"next":false,"comments":true,"date":"2020-05-26T21:34:41.000Z"},"headers":[],"relativePath":"posts/2020/05_26_Understand-Git.md","filePath":"posts/2020/05_26_Understand-Git.md","lastUpdated":1713886889000}');const r=t({name:"posts/2020/05_26_Understand-Git.md"},[["render",function(t,o,r,l,n,s){return a(),i("div",null,o[0]||(o[0]=[e('<h1 id="理解-git" tabindex="-1">理解 Git <a class="header-anchor" href="#理解-git" aria-label="Permalink to &quot;理解 Git&quot;">​</a></h1><blockquote><p>Git的学习曲线和难度相对较高,理解内部的原理和一些核心概念对使用git帮助极大</p></blockquote><h2 id="git学习的难度在哪里" tabindex="-1">Git学习的难度在哪里？ <a class="header-anchor" href="#git学习的难度在哪里" aria-label="Permalink to &quot;Git学习的难度在哪里？&quot;">​</a></h2><p><strong>命令多</strong>:tab键一个就200多个,选项更多</p><p><strong>一个命令多种用途</strong>:add命令</p><ul><li>跟踪文件</li><li>添加到暂存区</li><li>修改冲突文件后,用add来标记这个冲突已经解决</li></ul><p><strong>有些命令是组合命令</strong>:一个命令可以包含多个命令（隐藏操作）</p><p><strong>如果不明白原理,可以文档都看不懂</strong>:git help push</p><h2 id="认识git仓库-git目录" tabindex="-1">认识Git仓库.git目录 <a class="header-anchor" href="#认识git仓库-git目录" aria-label="Permalink to &quot;认识Git仓库.git目录&quot;">​</a></h2><h2 id="git的工作模型" tabindex="-1">Git的工作模型？ <a class="header-anchor" href="#git的工作模型" aria-label="Permalink to &quot;Git的工作模型？&quot;">​</a></h2><ul><li>Untrack -- Unmodified -- Modified -- Staged</li></ul><p><a href="https://git-scm.com/book/en/v2/Git-Basics-Recording-Changes-to-the-Repository" target="_blank" rel="noreferrer">https://git-scm.com/book/en/v2/Git-Basics-Recording-Changes-to-the-Repository</a></p><p><img src="https://git-scm.com/book/en/v2/images/lifecycle.webp" alt="" loading="lazy"></p><p><a href="https://git-scm.com/book/en/v2/Git-Branching-Branches-in-a-Nutshell" target="_blank" rel="noreferrer">https://git-scm.com/book/en/v2/Git-Branching-Branches-in-a-Nutshell</a></p><p><img src="https://git-scm.com/book/en/v2/images/branch-and-history.webp" alt="" loading="lazy"></p><p><img src="https://git-scm.com/book/en/v2/images/commit-and-tree.webp" alt="" loading="lazy"></p><p><strong>git中的三大对象objects</strong></p><ul><li>commit object:保存提交信息(项目快照,时间,作者,备注)</li><li>tree object:目录</li><li>blob:文件</li></ul><h2 id="git中的head指的是什么" tabindex="-1">Git中的HEAD指的是什么？ <a class="header-anchor" href="#git中的head指的是什么" aria-label="Permalink to &quot;Git中的HEAD指的是什么？&quot;">​</a></h2><ul><li>文件存储位置./.git/HEAD(linux:一切皆文件)</li><li>指向的是分支,而分支是指向当前分支的最后一个commit</li><li>代表当前工作目录中的文件是基于哪个commit的</li></ul><h2 id="git保存的是文件的修改还是文件的全部内容" tabindex="-1">Git保存的是文件的修改还是文件的全部内容？ <a class="header-anchor" href="#git保存的是文件的修改还是文件的全部内容" aria-label="Permalink to &quot;Git保存的是文件的修改还是文件的全部内容？&quot;">​</a></h2><blockquote><p>保存文件的全部内容</p></blockquote><h2 id="git是如何管理存储文件的" tabindex="-1">Git是如何管理存储文件的？ <a class="header-anchor" href="#git是如何管理存储文件的" aria-label="Permalink to &quot;Git是如何管理存储文件的？&quot;">​</a></h2><blockquote><p>对文件内容进行hash,用于目录和文件名,内容进行二进制缩,存储在.git/objecs目录下</p></blockquote><h2 id="git新建分支为什么非常的快" tabindex="-1">Git新建分支为什么非常的快？ <a class="header-anchor" href="#git新建分支为什么非常的快" aria-label="Permalink to &quot;Git新建分支为什么非常的快？&quot;">​</a></h2><blockquote><p>它不需要拷贝当前项目的所有文件,它只需要基于commit新建几个文件</p></blockquote><h2 id="git中隐藏的操作" tabindex="-1">Git中隐藏的操作 <a class="header-anchor" href="#git中隐藏的操作" aria-label="Permalink to &quot;Git中隐藏的操作&quot;">​</a></h2><ul><li>gc打包</li><li>zlib压缩</li></ul><p>hash-object</p><p>References:</p><ul><li><a href="https://git-scm.com/docs" target="_blank" rel="noreferrer">https://git-scm.com/docs</a></li><li><a href="https://git-scm.com/book/en/v2/Git-Internals-Plumbing-and-Porcelain" target="_blank" rel="noreferrer">https://git-scm.com/book/en/v2/Git-Internals-Plumbing-and-Porcelain</a></li><li><a href="https://github.com/git/git/blob/master/Documentation/technical" target="_blank" rel="noreferrer">https://github.com/git/git/blob/master/Documentation/technical</a></li><li><a href="https://www.youtube.com/results?search_query=git+internal" target="_blank" rel="noreferrer">https://www.youtube.com/results?search_query=git+internal</a></li><li><a href="https://mincong.io/2018/04/28/git-index/" target="_blank" rel="noreferrer">https://mincong.io/2018/04/28/git-index/</a></li></ul>',31)]))}]]);export{o as __pageData,r as default};
