import{_ as t,c as e,a2 as a,o as r}from"./chunks/framework.DRc6tsBz.js";const l=JSON.parse('{"title":"【Tool】Clang-tidy——静态代码分析框架","description":"","frontmatter":{"title":"【Tool】Clang-tidy——静态代码分析框架","categories":"Tools","tags":["Clang-tidy","LLVM"],"sidebar":false,"prev":false,"next":false,"comments":true,"date":"2021-08-29T10:42:22.000Z"},"headers":[],"relativePath":"posts/2021/08_29_Clang-tidy——静态代码分析框架.md","filePath":"posts/2021/08_29_Clang-tidy——静态代码分析框架.md","lastUpdated":1713284123000}');const o=t({name:"posts/2021/08_29_Clang-tidy——静态代码分析框架.md"},[["render",function(t,l,o,n,s,i){return r(),e("div",null,l[0]||(l[0]=[a('<h1 id="clang-tidy——静态代码分析框架" tabindex="-1">Clang-tidy——静态代码分析框架 <a class="header-anchor" href="#clang-tidy——静态代码分析框架" aria-label="Permalink to &quot;Clang-tidy——静态代码分析框架&quot;">​</a></h1><p>clang-tidy具体可以检测以下几个部分： （1）boost 检测boost库API使用问题 （2）cert 检测CERT的代码规范 （3）cpp-core-guidelines 检测是否违反cpp-core-guidelines （4）google 检测是否违反google code style （5）llvm 检测是否违反llvm code style （6）readability 检测代码上相关问题，但又不明确属于任何代码规范的 （7）misc 其它一些零碎的check （8）mpi 检测MPI API问题 （9）modernize 把C++03代码转换成C++11代码，使用C++11新特性 （10）performance 检测performance相关问题</p><p>官网：<a href="https://clang.llvm.org/extra/clang-tidy/" target="_blank" rel="noreferrer">https://clang.llvm.org/extra/clang-tidy/</a></p><p>实例：<a href="https://github.com/google/orbit/blob/main/.clang-tidy" target="_blank" rel="noreferrer">https://github.com/google/orbit/blob/main/.clang-tidy</a></p><p>参考： <a href="https://hokein.github.io/clang-tools-tutorial/clang-tidy.html" target="_blank" rel="noreferrer">https://hokein.github.io/clang-tools-tutorial/clang-tidy.html</a><a href="https://www.cxyzjd.com/article/ypshowm/100040729" target="_blank" rel="noreferrer">https://www.cxyzjd.com/article/ypshowm/100040729</a><a href="https://blog.csdn.net/u013187057/article/details/103052275" target="_blank" rel="noreferrer">https://blog.csdn.net/u013187057/article/details/103052275</a><a href="https://www.cxyzjd.com/article/wads23456/108009617" target="_blank" rel="noreferrer">https://www.cxyzjd.com/article/wads23456/108009617</a><a href="https://codeyarns.com/2019/01/28/how-to-use-clang-tidy/" target="_blank" rel="noreferrer">https://codeyarns.com/2019/01/28/how-to-use-clang-tidy/</a><a href="https://segmentfault.com/a/1190000007015981" target="_blank" rel="noreferrer">https://segmentfault.com/a/1190000007015981</a><a href="https://www.kdab.com/clang-tidy-part-1-modernize-source-code-using-c11c14/" target="_blank" rel="noreferrer">https://www.kdab.com/clang-tidy-part-1-modernize-source-code-using-c11c14/</a><a href="https://www.kdab.com/clang-tidy-part-2-integrate-qmake-and-other-build-systems-using-bear/" target="_blank" rel="noreferrer">https://www.kdab.com/clang-tidy-part-2-integrate-qmake-and-other-build-systems-using-bear/</a></p>',5)]))}]]);export{l as __pageData,o as default};
